import React, {Component, Fragment} from 'react';
import {Col, Container, Row} from "react-bootstrap";
import RestClient from "../../RestAPI/RestClient";
import AppUrl from "../../RestAPI/AppUrl";
import ReactHtmlParser from 'react-html-parser';
import Loader from "../Loader/Loader";
import WentWrong from "../WentWrong/WentWrong";

class RefundSection extends Component {
    constructor() {
        super();
        this.state = {
            refundDes: "",
            loading: true,
            error: false
        }
    }

    componentDidMount() {
        const sessionAboutData = sessionStorage.getItem('aboutData');
        if(sessionAboutData == null) {
            
        }
        RestClient.GetRequest(AppUrl.Information).then(result => {
            this.setState({refundDes: result[0]['refund'], loading:false})
        }).catch(err => {
            this.setState({error:true, loading:false})
        })
    }

    render() {
        if(this.state.error == true) {
            return <WentWrong />
        } else {
            if(this.state.loading == true) {
                return <Loader />
            } else {
                return (
                    <Fragment>
                        <Container className="mt-5">
                            <Row>
                                <Col sm={12} lg={12} md={12}>
                                    { ReactHtmlParser(this.state.refundDes) }
                                </Col>
                            </Row>
                        </Container>
                    </Fragment>
                );
            }
        }
    }
}

export default RefundSection;
