import axios from 'axios';
import React from "react";
class RestClient {

   static GetRequest=(getUrl)=>{
      return axios.get(getUrl).then(response=>{
           return response.data;

        }).catch(error=>{
           return null;
       });
    }

    static PostRequest() {
       axios.post()
    }
}

export default RestClient;
