import React, {Component,Fragment} from 'react';
import {Nav, Navbar} from "react-bootstrap";
import whiteLogo from '../../asset/image/navlogo.svg'
import blueLogo from '../../asset/image/navlogoScroll.svg'
import '../../asset/css/custom.css'
import '../../asset/css/bootstrap.min.css'
import {Link} from "react-router-dom";
class TopNavigation extends Component {
    constructor(props){
        super();
        this.state={
            navBarTitle:"navTitle",
            navBarLogo:[whiteLogo],
            navVariant:"dark",
            navBarBack:"navBackground",
            navBarItem:"navItem",
            pageTitle:props.title
        }
    }
    onScroll=()=>{
        if(window.scrollY>100){
            this.setState({navVariant:'light',navBarTitle:'navTitleScroll', navBarLogo:[blueLogo], navBarBack:'navBackgroundScroll',navBarItem:'navItemScroll'})
        }
        else if(window.scrollY<100){
            this.setState({navVariant:'dark' ,navBarTitle:'navTitle',navBarLogo:[whiteLogo],navBarBack:'navBackground',navBarItem:'navItem'})
        }
    }
    
    componentDidMount() {
        window.addEventListener('scroll',this.onScroll)
    }

    render() {
        return (
            <Fragment>
                <title>{this.state.pageTitle}</title>
                <Navbar variant={this.state.navVariant} className={this.state.navBarBack} fixed="top" collapseOnSelect expand="lg">
                    <Navbar.Brand ><Link className={this.state.navBarTitle} to="/"><img src={this.state.navBarLogo} alt="nav-brand"/> Abc Company</Link> </Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                        <Nav className="mr-auto">
                        </Nav>
                        <Nav className="mt-2 ml-2">
                        <Link exact activeStyle={{color:'#00a8ee'}} className={this.state.navBarItem} to="/">HOME</Link>
                          <Link exact activeStyle={{color:'#00a8ee'}} className={this.state.navBarItem} to="/service">SERVICES</Link>
                          <Link exact activeStyle={{color:'#00a8ee'}} className={this.state.navBarItem} to="/course">COURSES</Link>
                          <Link exact activeStyle={{color:'#00a8ee'}} className={this.state.navBarItem} to="/portfolio">PROJECTS</Link>
                          <Link exact activeStyle={{color:'#00a8ee'}} className={this.state.navBarItem} to="/contact">CONTACT</Link>
                          <Link exact activeStyle={{color:'#00a8ee'}} className={this.state.navBarItem} to="/about">ABOUT</Link>
                        </Nav>
                    </Navbar.Collapse>
                </Navbar>
            </Fragment>
        );
    }
}
export default TopNavigation;
